        '#include &lt;stdio.h&gt;\n#include &lt;stdlib.h&gt;\n\ntypedef struct Node{\n    int data;\n    struct Node* next;\n}Node;\n\ntypedef struct {\n    Node* head;\n}LinkedList;\n\n// 初始化单链表\nvoid initList(LinkedList* L){\n    L-&gt;head = NULL;\n}\n\n// 头插法建立单链表\nvoid createListHead(LinkedList* L){\n    int x;\n    printf(&quot;请输入元素的值（-1结束）：&quot;);\n    scanf(&quot;%d&quot;, &amp;x);\n    while(x != -1){\n        Node* newNode = (Node*)malloc(sizeof(Node));\n        newNode-&gt;data = x;\n        newNode-&gt;next = L-&gt;head;\n        L-&gt;head = newNode;\n        printf(&quot;请输入元素的值（-1结束）：&quot;);\n        scanf(&quot;%d&quot;, &amp;x);\n    }\n}\n\n// 尾插法建立单链表\nvoid createListTail(LinkedList* L){\n    int x;\n    Node* tail = NULL;\n    printf(&quot;请输入元素的值（-1结束）：&quot;);\n    scanf(&quot;%d&quot;, &amp;x);\n    while(x != -1){\n        Node* newNode = (Node*)malloc(sizeof(Node));\n        newNode-&gt;data = x;\n        newNode-&gt;next = NULL;\n        if(L-&gt;head == NULL){\n            L-&gt;head = newNode;\n            tail = newNode;\n        }\n        else{\n            tail-&gt;next = newNode;\n            tail = newNode;\n        }\n        printf(&quot;请输入元素的值（-1结束）：&quot;);\n        scanf(&quot;%d&quot;, &amp;x);\n    }\n}\n\n// 输出单链表的长度\nint getLength(LinkedList* L){\n    int length = 0;\n    Node* p = L-&gt;head;\n    while(p != NULL){\n        length++;\n        p = p-&gt;next;\n    }\n    return length;\n}\n\n// 输出单链表的第i个元素（按位置查找）\nint getElement(LinkedList* L, int i){\n    int index = 1;\n    Node* p = L-&gt;head;\n    while(p != NULL &amp;&amp; index &lt; i){\n        p = p-&gt;next;\n        index++;\n    }\n    if(p != NULL &amp;&amp; index == i){\n        return p-&gt;data;\n    }\n    else{\n        printf(&quot;第%d个元素不存在\n&quot;, i);\n        return -1;\n    }\n}\n\n// 输出给定元素的位置（按内容查找）\nint getPosition(LinkedList* L, int x){\n    int index = 1;\n    Node* p = L-&gt;head;\n    while(p != NULL &amp;&amp; p-&gt;data != x){\n        p = p-&gt;next;\n        index++;\n    }\n    if(p != NULL){\n        return index;\n    }\n    else{\n        return -1;\n    }\n}\n\n// 在第i个元素前插入给定元素（在第i个位置插入）\nvoid insertElement(LinkedList* L, int i, int x){\n    if(i &lt; 1 || i &gt; getLength(L)){\n        printf(&quot;插入位置不合法\n&quot;);\n        return;\n    }\n    Node* newNode = (Node*)malloc(sizeof(Node));\n    newNode-&gt;data = x;\n    if(i == 1){\n        newNode-&gt;next = L-&gt;head;\n        L-&gt;head = newNode;\n    }\n    else{\n        int index = 1;\n        Node* p = L-&gt;head;\n        while(p != NULL &amp;&amp; index &lt; i - 1){\n            p = p-&gt;next;\n            index++;\n        }\n        newNode-&gt;next = p-&gt;next;\n        p-&gt;next = newNode;\n    }\n}\n\n// 删除单链表L的第i个元素\nvoid deleteElement(LinkedList* L, int i){\n    if(i &lt; 1 || i &gt; getLength(L)){\n        printf(&quot;删除位置不合法\n&quot;);\n        return;\n    }\n    if(i == 1){\n        Node* temp = L-&gt;head;\n        L-&gt;head = L-&gt;head-&gt;next;\n        free(temp);\n    }\n    else{\n        int index = 1;\n        Node* p = L-&gt;head;\n        while(p != NULL &amp;&amp; index &lt; i - 1){\n            p = p-&gt;next;\n            index++;\n        }\n        Node* temp = p-&gt;next;\n        p-&gt;next = temp-&gt;next;\n        free(temp);\n    }\n}\n\n// 输出单链表\nvoid printList(LinkedList* L){\n    Node* p = L-&gt;head;\n    while(p != NULL){\n        printf(&quot;%d &quot;, p-&gt;data);\n        p = p-&gt;next;\n    }\n    printf(&quot;\n&quot;);\n}\n\n// 清空单链表（只保留头结点）\nvoid clearList(LinkedList* L){\n    Node* p = L-&gt;head;\n    while(p != NULL){\n        Node* temp = p;\n        p = p-&gt;next;\n        free(temp);\n    }\n    L-&gt;head = NULL;\n}\n\n// 单链表逆置\nvoid reverseList(LinkedList* L){\n    Node* prev = NULL;\n    Node* p = L-&gt;head;\n    while(p != NULL){\n        Node* temp = p-&gt;next;\n        p-&gt;next = prev;\n        prev = p;\n        p = temp;\n    }\n    L-&gt;head = prev;\n}\n\n// 建立一个非递减排序的单链表\nvoid sortList(LinkedList* L){\n    Node* p = L-&gt;head;\n    while(p != NULL &amp;&amp; p-&gt;next != NULL){\n        Node* q = p-&gt;next;\n        while(q != NULL){\n            if(p-&gt;data &gt; q-&gt;data){\n                int temp = p-&gt;data;\n                p-&gt;data = q-&gt;data;\n                q-&gt;data = temp;\n            }\n            q = q-&gt;next;\n        }\n        p = p-&gt;next;\n    }\n}\n\n// 菜单函数\nvoid menu(){\n    printf(&quot;**********菜单**********\n&quot;);\n    printf(&quot;1. 初始化单链表\n&quot;);\n    printf(&quot;2. 用头插法建立单链表\n&quot;);\n    printf(&quot;3. 用尾插法建立单链表\n&quot;);\n    printf(&quot;4. 输出单链表的长度\n&quot;);\n    printf(&quot;5. 输出单链表的第i个元素\n&quot;);\n    printf(&quot;6. 输出给定元素的位置\n&quot;);\n    printf(&quot;7. 在第i个元素前插入给定元素\n&quot;);\n    printf(&quot;8. 删除单链表的第i个元素\n&quot;);\n    printf(&quot;9. 输出单链表\n&quot;);\n    printf(&quot;10. 清空单链表\n&quot;);\n    printf(&quot;11. 单链表逆置\n&quot;);\n    printf(&quot;12. 建立非递减排序的单链表\n&quot;);\n    printf(&quot;0. 退出\n&quot;);\n    printf(&quot;*************************\n&quot;);\n}\n\nint main(){\n    LinkedList L;\n    initList(&amp;L);\n    int choice;\n    menu();\n    printf(&quot;请输入菜单选项：&quot;);\n    scanf(&quot;%d&quot;, &amp;choice);\n    while(choice != 0){\n        switch(choice){\n            case 1:\n                initList(&amp;L);\n                break;\n            case 2:\n                createListHead(&amp;L);\n                break;\n            case 3:\n                createListTail(&amp;L);\n                break;\n            case 4:\n                printf(&quot;单链表的长度为：%d\n&quot;, getLength(&amp;L));\n                break;\n            case 5:\n                printf(&quot;请输入要查找的位置：&quot;);\n                int pos;\n                scanf(&quot;%d&quot;, &amp;pos);\n                printf(&quot;第%d个元素的值为：%d\n&quot;, pos, getElement(&amp;L, pos));\n                break;\n            case 6:\n                printf(&quot;请输入要查找的元素：&quot;);\n                int element;\n                scanf(&quot;%d&quot;, &amp;element);\n                int position = getPosition(&amp;L, element);\n                if(position != -1){\n                    printf(&quot;元素%d的位置为：%d\n&quot;, element, position);\n                }\n                else{\n                    printf(&quot;元素%d不存在\n&quot;, element);\n                }\n                break;\n            case 7:\n                printf(&quot;请输入要插入的位置和元素（空格隔开）：&quot;);\n                int insertPos, insertElement;\n                scanf(&quot;%d %d&quot;, &amp;insertPos, &amp;insertElement);\n                insertElement(&amp;L, insertPos, insertElement);\n                break;\n            case 8:\n                printf(&quot;请输入要删除的位置：&quot;);\n                int deletePos;\n                scanf(&quot;%d&quot;, &amp;deletePos);\n                deleteElement(&amp;L, deletePos);\n                break;\n            case 9:\n                printList(&amp;L);\n                break;\n            case 10:\n                clearList(&amp;L);\n                break;\n            case 11:\n                reverseList(&amp;L);\n                break;\n            case 12:\n                sortList(&amp;L);\n                break;\n            default:\n                printf(&quot;输入错误，请重新输入\n&quot;);\n                break;\n        }\n        menu();\n        printf(&quot;请输入菜单选项：&quot;);\n        scanf(&quot;%d&quot;, &amp;choice);\n    }\n    return 0;\n}',
